SET NAMES UTF8;
DROP DATABASE IF EXISTS user;
CREATE DATABASE user CHARSET=utf8;
USE user;
CREATE TABLE reg(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    unum VARCHAR(11),
    upwd VARCHAR(32),
    ltime BIGINT
);
CREATE TABLE hl(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    jpg VARCHAR(200),
    url VARCHAR(200),
    i1 VARCHAR(200),
    i2 VARCHAR(200),
    i3 VARCHAR(200),
    i4 VARCHAR(200),
    i5 VARCHAR(200),
    i6 VARCHAR(200),
    i7 VARCHAR(200),
    i8 VARCHAR(200),
    i9 VARCHAR(200),
    i10 VARCHAR(200),
    i11 VARCHAR(200),
    i12 VARCHAR(200),
    i13 VARCHAR(200),
    i14 VARCHAR(200),
    i15 VARCHAR(200),
    i16 VARCHAR(200),
    i17 INT,
    i18 VARCHAR(200),
    i19 VARCHAR(200),
    i20 VARCHAR(200)
);
CREATE TABLE canvas(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    mouth VARCHAR(100),
    bar INT,
    line INT
);
